# Create sample users
1.upto(50) do |i|
  first_name = Faker::Name.first_name
  last_name = Faker::Name.last_name

  sample_user = User.new({first_name: first_name, last_name: last_name,
                          email: first_name + '.' + last_name + (i * 53).to_s + '@technikum-wien.at', password: '12345678',
                          password_confirmation: '12345678'})
  sample_user.add_role :user
  sample_user.skip_confirmation!
  sample_user.save!
end

